mapper.select:
SELECT DISTINCT(rp.shop_id) as shopId, rp.red_packet_id as redPacketId, rp.price as price, rp.amount as amount, rp.status as status, rp.begin_date as beginDate, rp.end_date as endDate, rp.source_order_id as sourceOrderId, rp.give_user_id as giveUserId, s.shop_name as shopName, rp.business_area_activity_id as businessAreaActivityId FROM 1dcq_red_packet rp INNER JOIN 1dcq_business_area_shop bas ON bas.business_area_activity_id = rp.business_area_activity_id INNER JOIN 1dcq_shop s ON rp.shop_id = s.shop_id WHERE rp.user_id = #{userId}
AND bas.shop_id = #{shopId}
AND find_in_set(rp.status, #{status}) > 0
AND rp.begin_date >= #{startDate}
AND rp.create_time <= #{endDate}
ORDER BY rp.create_time DESC limit #{limit},#{pSize}

SELECT 
   count(DISTINCT(rp.red_packet_id))
 FROM 1dcq_red_packet rp 
INNER JOIN 1dcq_business_area_shop bas
ON bas.business_area_activity_id = rp.business_area_activity_id
INNER  JOIN 1dcq_shop s 
ON rp.shop_id = s.shop_id
WHERE rp.user_id = #{userId}

	AND bas.shop_id = #{shopId}


	AND find_in_set(rp.status, #{status}) > 0


	AND rp.begin_date >= #{startDate}



		AND rp.end_date <= #{endDate}
